package week1day1;

public class Loopit {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Syntax of For Loop
		//for(int i=1;i<=5;i++)
		// Take second for in the list when given ctrl+space
		
		for (int i = 1; i < 6; i++) {
			System.out.println(i);
		}
		
		// Printing in Reverse Order
		
		for (int i = 6; i > 0; i--) {
			System.out.println(i);
		}
	}

}
